import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import io from 'socket.io-client';

const ChatManagement = () => {
  const [chats, setChats] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [currentEmployee, setCurrentEmployee] = useState(null);
  const socketRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Загружаем данные сотрудника при монтировании
    const employeeData = JSON.parse(localStorage.getItem('employeeData'));
    setCurrentEmployee(employeeData);

    // Инициализация WebSocket соединения
    socketRef.current = io('http://localhost:3001', {
      withCredentials: true,
      extraHeaders: {
        'Authorization': `Bearer ${localStorage.getItem('employeeToken')}`
      }
    });

    // Загрузка списка чатов
    const fetchChats = async () => {
      try {
        const response = await fetch('/api/chat/my', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('employeeToken')}`
          }
        });
        const data = await response.json();
        setChats(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchChats();

    // Обработчики событий WebSocket
    socketRef.current.on('new-message', (message) => {
      if (message.chat_id === selectedChat?.id) {
        setMessages(prev => [...prev, message]);
      }
    });

    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
    };
  }, []);

  useEffect(() => {
    if (selectedChat && socketRef.current) {
      // Присоединяемся к чату через WebSocket
      socketRef.current.emit('join-chat', selectedChat.id, currentEmployee?.id);

      // Загружаем историю сообщений
      const fetchMessages = async () => {
        try {
          const response = await fetch(`/api/chat/${selectedChat.id}/messages`, {
            headers: {
              'Authorization': `Bearer ${localStorage.getItem('employeeToken')}`
            }
          });
          const data = await response.json();
          setMessages(data);
        } catch (err) {
          setError(err.message);
        }
      };

      fetchMessages();
    }
  }, [selectedChat, currentEmployee]);

  const handleSendMessage = () => {
    if (!newMessage.trim() || !selectedChat || !currentEmployee) return;

    // Формируем временное сообщение для мгновенного отображения
    const tempMessage = {
      id: Date.now(), // временный ID
      employee_id: currentEmployee.id,
      chat_id: selectedChat.id,
      content: newMessage,
      time: new Date().toISOString(),
      first_name: currentEmployee.first_name,
      last_name: currentEmployee.last_name || '',
      type: 'employee'
    };

    // Добавляем сообщение в список сразу
    setMessages(prev => [...prev, tempMessage]);

    // Отправка сообщения через WebSocket
    socketRef.current.emit('send-message', {
      chatId: selectedChat.id,
      userId: currentEmployee.id,
      content: newMessage,
      isEmployee: true
    });

    setNewMessage('');
  };

  if (loading) return <div className="text-center py-8">Загрузка...</div>;

  return (
    <div className="max-w-6xl mx-auto py-8 px-4">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Управление чатами</h1>
        <button 
          onClick={() => navigate('/employee/dashboard')}
          className="bg-gray-600 hover:bg-gray-700 text-white px-4 py-2 rounded"
        >
          Назад
        </button>
      </div>

      {error && <div className="mb-4 p-2 bg-red-100 text-red-700 rounded">{error}</div>}

      <div className="flex h-[600px] border rounded-lg overflow-hidden">
        {/* Список чатов */}
        <div className="w-1/3 border-r bg-gray-50">
          <div className="p-4 border-b">
            <h2 className="font-semibold">Мои чаты</h2>
          </div>
          <div className="overflow-y-auto h-full">
            {chats.map(chat => (
              <div 
                key={chat.id} 
                className={`p-4 border-b cursor-pointer hover:bg-gray-100 ${selectedChat?.id === chat.id ? 'bg-blue-50' : ''}`}
                onClick={() => setSelectedChat(chat)}
              >
                <h3 className="font-medium">{chat.name}</h3>
                <p className="text-sm text-gray-500">ID: {chat.id}</p>
              </div>
            ))}
          </div>
        </div>

        {/* Окно чата */}
        <div className="w-2/3 flex flex-col">
          {selectedChat ? (
            <>
              <div className="p-4 border-b">
                <h2 className="font-semibold">{selectedChat.name}</h2>
              </div>
              
              {/* Сообщения */}
              <div className="flex-1 overflow-y-auto p-4 space-y-4">
                {messages.map((message, index) => (
                  <div 
                    key={index} 
                    className={`flex ${message.type === 'employee' ? 'justify-end' : 'justify-start'}`}
                  >
                    <div 
                      className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${message.type === 'employee' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
                    >
                      <div className="font-semibold">
                        {message.type === 'employee' 
                          ? `${currentEmployee?.first_name} ${currentEmployee?.last_name || ''}`
                          : `${message.first_name} ${message.last_name || ''}`}
                      </div>
                      <div>{message.content}</div>
                      <div className="text-xs opacity-70 mt-1">
                        {new Date(message.time).toLocaleString()}
                      </div>
                    </div>
                  </div>
                ))}
              </div>

              {/* Форма отправки сообщения */}
              <div className="p-4 border-t">
                <div className="flex space-x-2">
                  <input
                    type="text"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                    placeholder="Введите сообщение..."
                    className="flex-1 border rounded px-3 py-2"
                  />
                  <button
                    onClick={handleSendMessage}
                    className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
                  >
                    Отправить
                  </button>
                </div>
              </div>
            </>
          ) : (
            <div className="flex-1 flex items-center justify-center text-gray-500">
              Выберите чат для начала общения
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ChatManagement;
