import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../../AuthContext';
import io from 'socket.io-client';

const Chat = () => {
  const { user } = useAuth();
  const [chats, setChats] = useState([]);
  const [activeChat, setActiveChat] = useState(null);
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const socketRef = useRef(null);
  const messagesEndRef = useRef(null);

  const REACT_APP_API_URL = 'http://localhost:3001/';

  useEffect(() => {
    // Инициализация сокета
    socketRef.current = io(REACT_APP_API_URL || 'http://localhost:3001');

    // Получение списка чатов
    const fetchChats = async () => {
      try {
        const response = await fetch('/chat/my', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        const data = await response.json();
        setChats(data);
        if (data.length > 0) {
          setActiveChat(data[0].id);
        }
      } catch (error) {
        console.error('Ошибка при загрузке чатов:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchChats();

    // Обработчики событий сокета
    socketRef.current.on('new-message', (newMessage) => {
      setMessages(prev => [...prev, newMessage]);
      scrollToBottom();
    });

    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
    };
  }, []);

  useEffect(() => {
    if (activeChat) {
      joinChat(activeChat);
      fetchMessages(activeChat);
    }
  }, [activeChat]);

  const joinChat = (chatId) => {
    if (socketRef.current) {
      socketRef.current.emit('join-chat', chatId, user.id);
    }
  };

  const fetchMessages = async (chatId) => {
    try {
      const response = await fetch(`/chat/${chatId}/messages`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      const data = await response.json();
      setMessages(data);
      scrollToBottom();
    } catch (error) {
      console.error('Ошибка при загрузке сообщений:', error);
    }
  };

  const sendMessage = () => {
    if (message.trim() && activeChat && socketRef.current) {
      socketRef.current.emit('send-message', {
        chatId: activeChat,
        userId: user.id,
        content: message,
        isEmployee: user.role && user.role !== 'user'
      });
      setMessage('');
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const createNewChat = async () => {
    try {
      const response = await fetch('/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({ name: `Чат с поддержкой` })
      });
      const newChat = await response.json();
      setChats(prev => [...prev, newChat]);
      setActiveChat(newChat.id);
    } catch (error) {
      console.error('Ошибка при создании чата:', error);
    }
  };

  if (loading) {
    return <div className="text-center py-4">Загрузка чатов...</div>;
  }

  return (
    <div className="flex flex-col h-full">
      <div className="flex justify-between items-center mb-4">
        <h3 className="text-lg font-semibold">Мои чаты</h3>
        <button 
          onClick={createNewChat}
          className="bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded"
        >
          Новый чат
        </button>
      </div>

      <div className="flex flex-1 overflow-hidden">
        {/* Список чатов */}
        <div className="w-1/3 border-r overflow-y-auto">
          {chats.length === 0 ? (
            <div className="text-center py-4">Нет доступных чатов</div>
          ) : (
            chats.map(chat => (
              <div 
                key={chat.id}
                onClick={() => setActiveChat(chat.id)}
                className={`p-3 border-b cursor-pointer ${activeChat === chat.id ? 'bg-gray-100' : ''}`}
              >
                <div className="font-medium">{chat.name}</div>
                <div className="text-sm text-gray-500">
                  {new Date(chat.created_at).toLocaleString()}
                </div>
              </div>
            ))
          )}
        </div>

        {/* Окно сообщений */}
        <div className="flex-1 flex flex-col pl-4">
          {activeChat ? (
            <>
              <div className="flex-1 overflow-y-auto mb-4">
                {messages.map((msg, index) => (
                  <div 
                    key={index} 
                    className={`mb-3 p-2 rounded ${msg.user_id === user.id ? 'bg-blue-100 ml-auto max-w-xs' : 'bg-gray-100 mr-auto max-w-xs'}`}
                  >
                    <div className="font-semibold">
                      {msg.first_name} {msg.last_name}
                    </div>
                    <div>{msg.content}</div>
                    <div className="text-xs text-gray-500">
                      {new Date(msg.time).toLocaleTimeString()}
                    </div>
                  </div>
                ))}
                <div ref={messagesEndRef} />
              </div>

              <div className="flex">
                <input
                  type="text"
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                  placeholder="Введите сообщение..."
                  className="flex-1 border rounded-l p-2"
                />
                <button
                  onClick={sendMessage}
                  className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-r"
                >
                  Отправить
                </button>
              </div>
            </>
          ) : (
            <div className="text-center py-4">Выберите чат или создайте новый</div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Chat;
