import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import EmployeeForm from './components/EmployeeForm';
import EmployeeList from './components/EmployeeList';

const EmployeesManagement = () => {
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({
    email: '',
    first_name: '',
    last_name: '',
    password: '',
    role: 'manager'
  });
  const [editMode, setEditMode] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const token = localStorage.getItem('employeeToken');
        const response = await fetch('http://localhost:3001/employees', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        if (!response.ok) throw new Error('Ошибка загрузки сотрудников');
        
        const data = await response.json();
        setEmployees(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchEmployees();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('employeeToken');
      const url = editMode 
        ? `http://localhost:3001/employees/${editMode}`
        : 'http://localhost:3001/employees';
      
      const method = editMode ? 'PUT' : 'POST';
      const body = editMode 
        ? { ...formData, password: undefined }
        : formData;

      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(body)
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Ошибка сохранения');
      }

      const updatedEmployee = await response.json();
      
      if (editMode) {
        setEmployees(employees.map(emp => 
          emp.id === editMode ? updatedEmployee : emp
        ));
      } else {
        setEmployees([...employees, updatedEmployee]);
      }

      setFormData({
        email: '',
        first_name: '',
        last_name: '',
        password: '',
        role: 'manager'
      });
      setEditMode(null);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleEdit = (employee) => {
    setEditMode(employee.id);
    setFormData({
      email: employee.email,
      first_name: employee.first_name,
      last_name: employee.last_name,
      password: '',
      role: employee.role
    });
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Вы уверены, что хотите удалить этого сотрудника?')) return;
    
    try {
      const token = localStorage.getItem('employeeToken');
      const response = await fetch(`http://localhost:3001/employees/${id}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (!response.ok) throw new Error('Ошибка удаления');

      setEmployees(employees.filter(emp => emp.id !== id));
    } catch (err) {
      setError(err.message);
    }
  };

  if (loading) return <div className="text-center py-8">Загрузка...</div>;

  return (
    <div className="max-w-6xl mx-auto py-8 px-4">
      <h1 className="text-2xl font-bold mb-6">Управление сотрудниками</h1>
      
      {error && <div className="mb-4 p-2 bg-red-100 text-red-700 rounded">{error}</div>}

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        <EmployeeForm 
          formData={formData}
          editMode={editMode}
          handleInputChange={handleInputChange}
          handleSubmit={handleSubmit}
          setEditMode={setEditMode}
        />
        
        <EmployeeList 
          employees={employees}
          handleEdit={handleEdit}
          handleDelete={handleDelete}
        />
      </div>
    </div>
  );
};

export default EmployeesManagement;
