import React, { useState, useEffect } from 'react';
import EmployeeForm from './EmployeeForm';
import SectionCard from '../components/SectionCard';
import { useAuth } from '../../../../AuthContext';

const EmployeesManagement = () => {
  const { user } = useAuth();
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({
    email: '',
    first_name: '',
    last_name: '',
    password: '',
    role: 'manager'
  });
  const [editMode, setEditMode] = useState(null);

  useEffect(() => {
    if (user?.role !== 'admin') return;

    const fetchEmployees = async () => {
      try {
        const token = localStorage.getItem('employeeToken');
        const response = await fetch('http://localhost:3001/employees', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        if (!response.ok) throw new Error('Ошибка загрузки сотрудников');
        
        const data = await response.json();
        setEmployees(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchEmployees();
  }, [user]);

  if (user?.role !== 'admin') {
    return (
      <SectionCard title="Доступ запрещен">
        <p>У вас нет прав для просмотра этого раздела</p>
      </SectionCard>
    );
  }

  if (loading) {
    return (
      <SectionCard title="Загрузка...">
        <p>Загрузка данных сотрудников...</p>
      </SectionCard>
    );
  }

  return (
    <>
      <SectionCard title={editMode ? 'Редактирование сотрудника' : 'Добавление сотрудника'}>
        <EmployeeForm
          formData={formData}
          setFormData={setFormData}
          editMode={editMode}
          setEditMode={setEditMode}
          setEmployees={setEmployees}
          setError={setError}
        />
      </SectionCard>

      <SectionCard title="Список сотрудников" className="mt-6">
        {error && <div className="mb-4 p-2 bg-red-100 text-red-700 rounded">{error}</div>}
        
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            {/* Таблица сотрудников */}
          </table>
        </div>
      </SectionCard>
    </>
  );
};

export default EmployeesManagement;
