import React from 'react';
import { useAuth } from '../Auth/AuthContext';

const UserProfile = () => {
  const { user, logout } = useAuth();

  if (!user) {
    return <div className="text-center mt-10">Пожалуйста, войдите в систему</div>;
  }

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-6 text-center">Личный кабинет</h2>
      <div className="space-y-4">
        <div>
          <p className="font-semibold">Имя:</p>
          <p>{user.first_name} {user.last_name}</p>
        </div>
        <div>
          <p className="font-semibold">Email:</p>
          <p>{user.email}</p>
        </div>
        <div>
          <p className="font-semibold">Возраст:</p>
          <p>{user.age}</p>
        </div>
        <button
          onClick={logout}
          className="w-full bg-red-600 text-white py-2 px-4 rounded hover:bg-red-700 mt-6"
        >
          Выйти
        </button>
      </div>
    </div>
  );
};

export default UserProfile;
