// AuthPage.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Layout from "./Layout";

const AuthPage = ({ setToken }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [age, setAge] = useState(18);
  const [isLogin, setIsLogin] = useState(true);
  const [errors, setErrors] = useState([]);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setErrors([]);

    try {
      const endpoint = isLogin ? "/auth/login" : "/auth/register";
      const body = isLogin 
        ? { email, password }
        : { email, password, first_name: firstName, last_name: lastName, age };

      const response = await fetch(`http://localhost:3001${endpoint}`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(body),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || "Ошибка авторизации");
      }

      if (isLogin) {
        localStorage.setItem("token", data.token);
        setToken(data.token);
        navigate("/");
      } else {
        setIsLogin(true);
        setErrors([{ message: "Регистрация успешна. Теперь вы можете войти." }]);
      }
    } catch (error) {
      setErrors([{ message: error.message }]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Layout>
      <main className="container mx-auto py-12 px-6">
        <h2 className="text-2xl md:text-4xl font-bold mb-6 text-center">
          {isLogin ? "Вход в систему" : "Регистрация"}
        </h2>
        
        <div className="max-w-md mx-auto bg-white shadow-lg rounded-lg p-8">
          {errors.length > 0 && (
            <div className="mb-4 p-4 bg-red-100 border border-red-400 text-red-700 rounded">
              {errors.map((error, index) => (
                <p key={index}>{error.message}</p>
              ))}
            </div>
          )}
          
          <form onSubmit={handleSubmit}>
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700">Email:</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                className="mt-1 block w-full border border-gray-300 rounded-md p-2"
              />
            </div>
            
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700">Пароль:</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                minLength="6"
                className="mt-1 block w-full border border-gray-300 rounded-md p-2"
              />
            </div>
            
            {!isLogin && (
              <>
                <div className="mb-4">
                  <label className="block text-sm font-medium text-gray-700">Имя:</label>
                  <input
                    type="text"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                    required
                    className="mt-1 block w-full border border-gray-300 rounded-md p-2"
                  />
                </div>
                
                <div className="mb-4">
                  <label className="block text-sm font-medium text-gray-700">Фамилия:</label>
                  <input
                    type="text"
                    value={lastName}
                    onChange={(e) => setLastName(e.target.value)}
                    required
                    className="mt-1 block w-full border border-gray-300 rounded-md p-2"
                  />
                </div>
                
                <div className="mb-4">
                  <label className="block text-sm font-medium text-gray-700">Возраст:</label>
                  <input
                    type="number"
                    value={age}
                    onChange={(e) => setAge(e.target.value)}
                    min="18"
                    max="120"
                    required
                    className="mt-1 block w-full border border-gray-300 rounded-md p-2"
                  />
                </div>
              </>
            )}
            
            <button
              type="submit"
              disabled={loading}
              className="w-full mt-4 bg-blue-500 text-white p-2 rounded hover:bg-blue-600 transition duration-200 disabled:bg-blue-300"
            >
              {loading ? "Загрузка..." : isLogin ? "Войти" : "Зарегистрироваться"}
            </button>
          </form>
          
          <div className="mt-4 text-center">
            <button
              onClick={() => setIsLogin(!isLogin)}
              className="text-blue-500 hover:text-blue-700"
            >
              {isLogin 
                ? "Нет аккаунта? Зарегистрироваться" 
                : "Уже есть аккаунт? Войти"}
            </button>
          </div>
        </div>
      </main>
    </Layout>
  );
};

export default AuthPage;
