import React, { useState } from 'react';
import { createEmployee, updateEmployee, updateEmployeePassword } from '../services/EmployeeService';

const EmployeeForm = ({ formData, editMode, handleInputChange, handleSubmit, setEditMode }) => {
  const [password, setPassword] = useState('');
  const [isPasswordModalOpen, setIsPasswordModalOpen] = useState(false);
  const [error, setError] = useState('');

  const handlePasswordChange = async (e) => {
    e.preventDefault();
    try {
      await updateEmployeePassword(editMode, password);
      setIsPasswordModalOpen(false);
      setPassword('');
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="bg-white rounded-lg shadow p-6">
      <h2 className="text-xl font-semibold mb-4">
        {editMode ? 'Редактировать сотрудника' : 'Добавить сотрудника'}
      </h2>
      {error && <div className="mb-4 p-2 bg-red-100 text-red-700 rounded">{error}</div>}
      
      <form onSubmit={handleSubmit}>
        <div className="space-y-4">
          {/* Остальные поля формы остаются без изменений */}
          {/* ... */}

          {editMode && (
            <div className="pt-2">
              <button
                type="button"
                onClick={() => setIsPasswordModalOpen(true)}
                className="text-blue-600 hover:text-blue-800 text-sm"
              >
                Изменить пароль
              </button>
            </div>
          )}
        </div>
      </form>

      {/* Модальное окно для смены пароля */}
      {isPasswordModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg shadow-xl p-6 w-full max-w-md">
            <h3 className="text-lg font-semibold mb-4">Изменение пароля</h3>
            <form onSubmit={handlePasswordChange}>
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-1">Новый пароль</label>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="w-full px-3 py-2 border rounded"
                  required
                />
              </div>
              <div className="flex justify-end space-x-2">
                <button
                  type="button"
                  onClick={() => {
                    setIsPasswordModalOpen(false);
                    setPassword('');
                    setError('');
                  }}
                  className="px-4 py-2 border rounded"
                >
                  Отмена
                </button>
                <button
                  type="submit"
                  className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
                >
                  Сохранить
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default EmployeeForm;
