import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getDocuments, createDocument, updateDocumentLink } from './services/DocumentService';
import { getUsers } from './services/UserService';
import DocumentList from './components/Documents/DocumentList';
import DocumentForm from './components/Documents/DocumentForm';

const DocumentsManagement = () => {
  const [documents, setDocuments] = useState([]);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({
    user_id: '',
    document_name: '',
    description: '',
    link: ''
  });
  const [editMode, setEditMode] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [docsData, usersData] = await Promise.all([
          getDocuments(),
          getUsers()
        ]);
        setDocuments(docsData);
        setUsers(usersData);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleCreateSubmit = async (e) => {
    e.preventDefault();
    try {
      const newDocument = await createDocument(formData);
      setDocuments([...documents, newDocument]);
      setFormData({
        user_id: '',
        document_name: '',
        description: '',
        link: ''
      });
    } catch (err) {
      setError(err.message);
    }
  };

  const handleLinkUpdate = async (e) => {
    e.preventDefault();
    try {
      const updatedDoc = await updateDocumentLink(editMode.id, formData.link);
      setDocuments(documents.map(doc => 
        doc.id === editMode.id ? { ...doc, link: updatedDoc.link } : doc
      ));
      setEditMode(null);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleEditLink = (document) => {
    setEditMode(document);
    setFormData({
      link: document.link || ''
    });
  };

  const handleCancel = () => {
    setEditMode(null);
    setFormData({
      user_id: '',
      document_name: '',
      description: '',
      link: ''
    });
  };

  if (loading) return <div className="text-center py-8">Загрузка...</div>;

  return (
    <div className="max-w-6xl mx-auto py-8 px-4">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Управление документами</h1>
        {!editMode && (
          <button
            onClick={() => setEditMode('create')}
            className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
          >
            Добавить документ
          </button>
        )}
      </div>

      {error && <div className="mb-4 p-2 bg-red-100 text-red-700 rounded">{error}</div>}

      {editMode === 'create' ? (
        <DocumentForm
          documentData={formData}
          users={users}
          onChange={handleInputChange}
          onSubmit={handleCreateSubmit}
          onCancel={handleCancel}
        />
      ) : editMode && typeof editMode === 'object' ? (
        <DocumentForm
          documentData={formData}
          onChange={handleInputChange}
          onSubmit={handleLinkUpdate}
          onCancel={handleCancel}
          isLinkEdit={true}
        />
      ) : (
        <DocumentList 
          documents={documents} 
          onEditLink={handleEditLink}
        />
      )}
    </div>
  );
};

export default DocumentsManagement;
