import React, { useState } from 'react';
import EmployeeTabs from './EmployeeTabs';
import EmployeeProfile from './Profile';
import EmployeesManagement from './Management';
import { useAuth } from '../../AuthContext';

const EmployeeDashboard = () => {
  const { logout } = useAuth();
  const [activeTab, setActiveTab] = useState('profile');

  const tabs = [
    { id: 'profile', label: 'Мой профиль' },
    { id: 'management', label: 'Управление сотрудниками' },
  ];

  const renderTabContent = () => {
    switch (activeTab) {
      case 'profile':
        return <EmployeeProfile />;
      case 'management':
        return <EmployeesManagement />;
      default:
        return <EmployeeProfile />;
    }
  };

  return (
    <div className="max-w-4xl mx-auto mt-10 p-6 bg-white rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-6 text-center">Панель управления</h2>
      
      <EmployeeTabs 
        tabs={tabs} 
        activeTab={activeTab} 
        onTabChange={setActiveTab} 
      />
      
      <div className="mt-6">
        {renderTabContent()}
      </div>
      
      <button
        onClick={logout}
        className="w-full bg-red-600 hover:bg-red-700 text-white font-bold py-2 px-4 rounded mt-8"
      >
        Выйти
      </button>
    </div>
  );
};

export default EmployeeDashboard;
