import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../../AuthContext';
import io from 'socket.io-client';

const Chat = () => {
  const { user } = useAuth();
  const [chats, setChats] = useState([]);
  const [activeChat, setActiveChat] = useState(null);
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const socketRef = useRef(null);
  const messagesEndRef = useRef(null);

  const API_URL = 'http://localhost:3001';

  useEffect(() => {
    // Инициализация сокета
    socketRef.current = io(API_URL || 'http://localhost:3001');

    // Получение списка чатов
    const fetchChats = async () => {
      try {
        const response = await fetch(`${API_URL}/chat/my`, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        const data = await response.json();
        setChats(data);
        if (data.length > 0) {
          setActiveChat(data[0].id);
        }
      } catch (error) {
        console.error('Ошибка при загрузке чатов:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchChats();

    // Обработчики событий сокета
    socketRef.current.on('new-message', (newMessage) => {
      setMessages(prev => [...prev, newMessage]);
      scrollToBottom();
    });

    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
    };
  }, []);

  useEffect(() => {
    if (activeChat) {
      joinChat(activeChat);
      fetchMessages(activeChat);
    }
  }, [activeChat]);

  const joinChat = (chatId) => {
    if (socketRef.current) {
      socketRef.current.emit('join-chat', chatId, user.id);
    }
  };

  const fetchMessages = async (chatId) => {
    try {
      const response = await fetch(`${API_URL}/chat/${chatId}/messages`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      const data = await response.json();
      setMessages(data);
      scrollToBottom();
    } catch (error) {
      console.error('Ошибка при загрузке сообщений:', error);
    }
  };

  const sendMessage = () => {
    if (message.trim() && activeChat && socketRef.current) {
      socketRef.current.emit('send-message', {
        chatId: activeChat,
        userId: user.id,
        content: message,
        isEmployee: user.role && user.role !== 'user'
      });
      setMessage('');
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const createNewChat = async () => {
    try {
      const response = await fetch(`${API_URL}/chat`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({ name: `Чат с поддержкой` })
      });
      const newChat = await response.json();
      setChats(prev => [...prev, newChat]);
      setActiveChat(newChat.id);
    } catch (error) {
      console.error('Ошибка при создании чата:', error);
    }
  };

  if (loading) {
    return <div className="text-center py-4">Загрузка чатов...</div>;
  }

  return (
    <div className="flex flex-col h-full">
      {error && <div className="mb-4 p-2 bg-red-100 text-red-700 rounded">{error}</div>}

      <div className="flex justify-between items-center mb-4">
        <h3 className="text-lg font-semibold">Мои чаты</h3>
        <button 
          onClick={handleCreateChat}
          className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
        >
          Новый чат
        </button>
      </div>

      <div className="flex h-[500px] border rounded-lg overflow-hidden">
        {/* Список чатов */}
        <div className="w-1/3 border-r bg-gray-50">
          <div className="p-4 border-b">
            <h2 className="font-semibold">Доступные чаты</h2>
          </div>
          <div className="overflow-y-auto h-full">
            {chats.length === 0 ? (
              <div className="p-4 text-center text-gray-500">Нет доступных чатов</div>
            ) : (
              chats.map(chat => (
                <div 
                  key={chat.id} 
                  className={`p-4 border-b cursor-pointer hover:bg-gray-100 ${selectedChat?.id === chat.id ? 'bg-blue-50' : ''}`}
                  onClick={() => setSelectedChat(chat)}
                >
                  <h3 className="font-medium">{chat.name}</h3>
                  <p className="text-sm text-gray-500">ID: {chat.id}</p>
                </div>
              ))
            )}
          </div>
        </div>

        {/* Окно чата */}
        <div className="w-2/3 flex flex-col">
          {selectedChat ? (
            <>
              <div className="p-4 border-b">
                <h2 className="font-semibold">{selectedChat.name}</h2>
              </div>
              
              {/* Сообщения */}
              <div className="flex-1 overflow-y-auto p-4 space-y-4">
                {messages.map((message, index) => (
                  <div 
                    key={index} 
                    className={`flex ${message.user_id === user.id ? 'justify-end' : 'justify-start'}`}
                  >
                    <div 
                      className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${message.user_id === user.id ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
                    >
                      <div className="font-semibold">
                        {message.first_name} {message.last_name}
                      </div>
                      <div>{message.content}</div>
                      <div className="text-xs opacity-70 mt-1">
                        {new Date(message.time).toLocaleString()}
                      </div>
                    </div>
                  </div>
                ))}
              </div>

              {/* Форма отправки сообщения */}
              <div className="p-4 border-t">
                <div className="flex space-x-2">
                  <input
                    type="text"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                    placeholder="Введите сообщение..."
                    className="flex-1 border rounded px-3 py-2"
                  />
                  <button
                    onClick={handleSendMessage}
                    className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
                  >
                    Отправить
                  </button>
                </div>
              </div>
            </>
          ) : (
            <div className="flex-1 flex items-center justify-center text-gray-500">
              Выберите чат для начала общения
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Chat;
