import React from 'react';
import { useAuth } from './AuthContext';

const Profile = () => {
  const { user, logout } = useAuth();

  return (
    <div className="max-w-2xl mx-auto mt-10 p-6 bg-white rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-6 text-center">Личный кабинет</h2>
      <div className="space-y-4">
        <div className="flex justify-between border-b pb-2">
          <span className="font-semibold">Email:</span>
          <span>{user.email}</span>
        </div>
        <div className="flex justify-between border-b pb-2">
          <span className="font-semibold">Имя:</span>
          <span>{user.first_name}</span>
        </div>
        <div className="flex justify-between border-b pb-2">
          <span className="font-semibold">Фамилия:</span>
          <span>{user.last_name}</span>
        </div>
        <div className="flex justify-between border-b pb-2">
          <span className="font-semibold">Возраст:</span>
          <span>{user.age}</span>
        </div>
      </div>
      <button
        onClick={logout}
        className="mt-8 w-full bg-red-600 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
      >
        Выйти
      </button>
    </div>
  );
};

export default Profile;
