import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import io from 'socket.io-client';

const ChatManagement = () => {
  const [chats, setChats] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const socketRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Инициализация WebSocket соединения
    socketRef.current = io('http://localhost:3001'); // Замените на ваш URL сервера

    // Загрузка списка чатов
    const fetchChats = async () => {
        try {
          const response = await fetch('http://localhost:3001/chat/my', { // Добавьте полный URL
            headers: {
              'Authorization': `Bearer ${localStorage.getItem('employeeToken')}`
            }
          });
          
          if (!response.ok) {
            const errorText = await response.text();
            throw new Error(`Ошибка ${response.status}: ${errorText}`);
          }
          
          const data = await response.json();
          setChats(data);
        } catch (err) {
          setError(err.message);
          console.error('Ошибка загрузки чатов:', err);
        } finally {
          setLoading(false);
        }
      };

    fetchChats();

    // Обработчики событий WebSocket
    socketRef.current.on('new-message', (message) => {
      if (message.chat_id === selectedChat?.id) {
        setMessages(prev => [...prev, message]);
      }
    });

    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
    };
  }, []);

  useEffect(() => {
    if (selectedChat) {
      // Присоединяемся к чату через WebSocket
      socketRef.current.emit('join-chat', selectedChat.id, localStorage.getItem('employeeId'));

      // Загружаем историю сообщений
      const fetchMessages = async () => {
        try {
          const response = await fetch(`http://localhost:3001/chat/${selectedChat.id}/messages`, {
            headers: {
              'Authorization': `Bearer ${localStorage.getItem('employeeToken')}`
            }
          });
          const data = await response.json();
          setMessages(data);
        } catch (err) {
          setError(err.message);
        }
      };

      fetchMessages();
    }
  }, [selectedChat]);

  const handleSendMessage = async () => {
    if (!newMessage.trim() || !selectedChat) return;
  
    const employeeId = localStorage.getItem('employeeId');
    const messageData = {
      chatId: selectedChat.id,
      userId: employeeId,
      employeeId: employeeId,
      content: newMessage,
      isEmployee: true
    };
  
    try {
      // 1. Отправляем сообщение через REST API для сохранения в БД
      const response = await fetch(`http://localhost:3001/chat/${selectedChat.id}/messages`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('employeeToken')}`
        },
        body: JSON.stringify(messageData)
      });
  
      if (!response.ok) {
        throw new Error('Ошибка при сохранении сообщения');
      }
  
      // 2. Отправляем через WebSocket для реального времени
      socketRef.current.emit('send-message', messageData);
      
      // 3. Опционально: добавляем сообщение локально (можно удалить, если сервер пришлет его обратно через сокет)
      const savedMessage = await response.json();
      setMessages(prev => [...prev, savedMessage]);
      
      setNewMessage('');
    } catch (err) {
      setError(err.message);
      console.error('Ошибка отправки сообщения:', err);
    }
  };
  const handleCreateChat = async (userId) => {
    try {
      const response = await fetch('/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('employeeToken')}`
        },
        body: JSON.stringify({ userId })
      });
      const data = await response.json();
      setChats([...chats, data]);
      setSelectedChat(data);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleAddUserToChat = async (userId) => {
    try {
      await fetch(`http://localhost:3001/chat/${selectedChat.id}/add-user`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('employeeToken')}`
        },
        body: JSON.stringify({ userId })
      });
      // Обновляем список участников или показываем уведомление
    } catch (err) {
      setError(err.message);
    }
  };

  if (loading) return <div className="text-center py-8">Загрузка...</div>;

  return (
    <div className="max-w-6xl mx-auto py-8 px-4">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Управление чатами</h1>
        <button 
          onClick={() => navigate('/employee/dashboard')}
          className="bg-gray-600 hover:bg-gray-700 text-white px-4 py-2 rounded"
        >
          Назад
        </button>
      </div>

      {error && <div className="mb-4 p-2 bg-red-100 text-red-700 rounded">{error}</div>}

      <div className="flex h-[600px] border rounded-lg overflow-hidden">
        {/* Список чатов */}
        <div className="w-1/3 border-r bg-gray-50">
          <div className="p-4 border-b">
            <h2 className="font-semibold">Мои чаты</h2>
          </div>
          <div className="overflow-y-auto h-full">
            {chats.map(chat => (
              <div 
                key={chat.id} 
                className={`p-4 border-b cursor-pointer hover:bg-gray-100 ${selectedChat?.id === chat.id ? 'bg-blue-50' : ''}`}
                onClick={() => setSelectedChat(chat)}
              >
                <h3 className="font-medium">{chat.name}</h3>
                <p className="text-sm text-gray-500">ID: {chat.id}</p>
              </div>
            ))}
          </div>
        </div>

        {/* Окно чата */}
        <div className="w-2/3 flex flex-col">
          {selectedChat ? (
            <>
              <div className="p-4 border-b">
                <h2 className="font-semibold">{selectedChat.name}</h2>
              </div>
              
              {/* Сообщения */}
              <div className="flex-1 overflow-y-auto p-4 space-y-4">
                {messages.map((message, index) => (
                  <div 
                    key={index} 
                    className={`flex ${message.type === 'employee' ? 'justify-end' : 'justify-start'}`}
                  >
                    <div 
                      className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${message.type === 'employee' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
                    >
                      <div className="font-semibold">
                        {message.first_name} {message.last_name}
                      </div>
                      <div>{message.content}</div>
                      <div className="text-xs opacity-70 mt-1">
                        {new Date(message.time).toLocaleString()}
                      </div>
                    </div>
                  </div>
                ))}
              </div>

              {/* Форма отправки сообщения */}
              <div className="p-4 border-t">
                <div className="flex space-x-2">
                  <input
                    type="text"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                    placeholder="Введите сообщение..."
                    className="flex-1 border rounded px-3 py-2"
                  />
                  <button
                    onClick={handleSendMessage}
                    className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
                  >
                    Отправить
                  </button>
                </div>
              </div>
            </>
          ) : (
            <div className="flex-1 flex items-center justify-center text-gray-500">
              Выберите чат для начала общения
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ChatManagement;
